#Mars Manon L3 ST #Atelier de programmation 0 #Exercice 1import randomcontinuer_ou_arreter = input("Voulez-vous jouer contre l'ordinateur (Max 5 parties) O/N ? " )if continuer_ou_arreter != 'O' :    if continuer_ou_arreter != 'N' :        print("Je n'ai pas compris votre réponse")if continuer_ou_arreter == 'O':    nom_joueur_1 = input("Quel est votre nom ? ")    print("Bienvenu ",nom_joueur_1, " nous allons jouer ensemble \n")    nom_joueur_2 = 'Machine'score_joueur_1 = 0nombre_parties = 0if continuer_ou_arreter == 'N':    nom_joueur_1 = input("Quel est votre nom ? ")    print("Bienvenu ",nom_joueur_1, " nous allons jouer ensemble")    nom_joueur_2 = input("Quel est le nom du deuxième joueur ?")    print("Bienvenu ",nom_joueur_2, " nous allons jouer ensemble \n")on_continue = Truescore_joueur_2 = 0while on_continue :    nombre_parties += 1     choix_joueur_1 = input("{nom} faîtes votre choix parmi (pierre, papier, ciseaux, puit): ".format(nom=nom_joueur_1))    if choix_joueur_1 != 'pierre':        if choix_joueur_1 != 'papier':            if choix_joueur_1 != 'ciseaux':                if choix_joueur_1 != 'puit' :                    choix_joueur_1ok = False                    print("Je n'ai pas compris votre réponse")                    while choix_joueur_1ok == False :                        print("Joueur ", choix_joueur_1)                        choix_joueur_1 = input(" faîtes votre choix parmi (pierre, papier, ciseaux): ")                        choix_joueur_1ok = True                        if choix_joueur_1 != 'pierre':                             if choix_joueur_1 != 'papier':                                if choix_joueur_1 != 'ciseaux':                                    if choix_joueur_1 != 'puit':                                        choix_joueur_1ok = False    if nom_joueur_2 == 'Machine':         #Ici il faudrait plutôt vérifier que continuer_ou_arreter == 'O' autrement        #il y a un problème si le joueur 2 s'appelle Machine !        choix_joueur_2 = ['papier','pierre','ciseaux', 'puit'][random.randint(0, 3)]    if nom_joueur_2 != 'Machine':        print("Joueur", nom_joueur_2)        choix_joueur_2 = input("faîtes votre choix parmi (pierre, papier, ciseaux, 'puit'): ")        if choix_joueur_2 != 'pierre':            if choix_joueur_2 != 'papier':                if choix_joueur_2 != 'ciseaux':                    if choix_joueur_2 != 'puit':                        machine_bon = False                        print("Je n'ai pas compris votre réponse")                        while not machine_bon :                            print("Joueur ", nom_joueur_2)                            choix_joueur_2 = input(" faîtes votre choix parmi (pierre, papier, ciseaux): ")                            machine_bon = True                            if choix_joueur_2 != 'pierre':                                 if choix_joueur_2 != 'papier':                                    if choix_joueur_2 != 'ciseaux':                                        if choix_joueur_2 != 'puit':                                            machine_bon = False    #On affiche les choix     print("Si on récapitule :",nom_joueur_1, choix_joueur_1, "et", nom_joueur_2, choix_joueur_2,"\n")    #On regarde qui a gagné cette manche on calcule les points et on affiche le résultat    if choix_joueur_1 == choix_joueur_2 :        gagnant_manche = "aucun de vous, vous être ex æquo"         score_joueur_1 = score_joueur_1 + 0        score_joueur_2 = score_joueur_2 + 0                #ici toutes les configurations où le joueur 1 gagne     if (choix_joueur_1 == 'pierre' and choix_joueur_2 == 'ciseaux') or (choix_joueur_1 == 'puit' and choix_joueur_2 == 'ciseaux') or (choix_joueur_1 == 'puit' and choix_joueur_2 == 'pierre') or (choix_joueur_1 == 'papier' and choix_joueur_2 == 'pierre') or (choix_joueur_1 == 'papier' and choix_joueur_2 == 'puit') or (choix_joueur_1 == 'ciseaux' and choix_joueur_2 == 'papier') :             gagnant_manche = nom_joueur_1            score_joueur_1 = score_joueur_1 + 1            score_joueur_2 = score_joueur_2 + 0                #ici le joueur 2 gagne     else :         gagnant_manche = nom_joueur_2        score_joueur_1 = score_joueur_1 + 0        score_joueur_2 = score_joueur_2 + 1               print("le gagnant est",gagnant_manche)      print("Les scores à l'issue de cette manche sont donc",nom_joueur_1, score_joueur_1, "et", nom_joueur_2, score_joueur_2, "\n")        if nombre_parties < 5:        on_continue = True        recommencer = input("Souhaitez vous refaire une partie {} contre {} ? (O/N) ".format(nom_joueur_1,nom_joueur_2))        if recommencer == 'O':            on_continue = True        if recommencer == 'N':            on_continue = False            print("Merci d'avoir joué ! A bientôt")                    else:             on_continue = True            print("Vous ne répondez pas à la question, on continue ")    else :         on_continue = False        print("Merci d'avoir joué ! A bientôt")         